var data = flow.get('formData') || {};

// get data
if (msg.topic === 'JobName') {
    data.JobName = msg.payload;
} else if (msg.topic === 'ProductName') {
    data.ProductName = msg.payload;
} else if (msg.topic === 'ProductDescription') {
    data.ProductDescription = msg.payload;
} else if (msg.topic === 'JobGoodPartsCounter') {
    data.JobGoodPartsCounter = msg.payload;
} else if (msg.topic === 'JobBadPartsCounter') {
    data.JobBadPartsCounter = msg.payload;
} else if (msg.topic === 'JobQuantityPlanned') {
    data.JobQuantityPlanned = msg.payload;
}


flow.set('formData', data);

if(msg.topic === 'SetJobData') {
    msg.data = data;
    flow.set('formData', {});
    return msg;
}
